<?phpnamespace Recipy\Db;/** * Class db * @package db */class DbManager{    /**     * @var string     */    public static $db = 'DbMain';    private static $instance = array();    /**     * @param null $name     *     * @return \PDO     */    public static function getInstanceOf($name = null)    {        if (empty($name))            $name = self::$db;        if (!isset(self::$instance[$name]) || empty(self::$instance[$name])) {            $nsName = __NAMESPACE__ . '\\' . $name;            self::$instance[$name] = new $nsName();            self::$instance[$name]->setAttribute(\PDO::ATTR_ERRMODE, \PDO::ERRMODE_WARNING);        }        return self::$instance[$name];    }}